import classes from '../grid/gridLayout.module.css';

function Item(props){
    return <li className={classes.item}>
            <div className={classes.card}>
                <div className={classes.image}>
                    <img src={props.imageURL} alt={props.title} />
                </div>
                <div className={classes.content}>
                    <h3>
                        {props.title}
                    </h3>
                    <p>
                        {props.description}
                    </p>
                </div>
            </div>
        </li>;
}

export default Item;
